// Stable script v5 - robust upload & preview handling, platform-specific ZIP generation
const fileInput = document.getElementById('file-input');
const dropArea = document.getElementById('drop-area');
const dropText = document.getElementById('drop-text');
const generateBtn = document.getElementById('generate-btn');
const addSizeBtn = document.getElementById('add-size-btn');
const newSizeInput = document.getElementById('new-size');
const squarePreview = document.getElementById('square-preview');
const circlePreview = document.getElementById('circle-preview');

let uploadedImage = null;
let sizes = [16,24,32,36,48,57,64,72,96,114,128,144,192,256,320,400,512,800,1024,2048];

// Embedded masks (base64)
const squareMaskSrc = "data:image/png;base64,";
const circularMaskSrc = "data:image/png;base64,";
const iosSquircleSrc = "data:image/png;base64,";

// Platform size sets (exact lists requested)
const iosExact = [60,120,180,76,167,1024];
const androidExact = [48,72,96,144,192,512];

function loadImage(src){
  return new Promise((res,rej)=>{
    const i = new Image();
    i.crossOrigin = 'anonymous';
    i.onload = () => res(i);
    i.onerror = () => rej(new Error('Failed to load ' + src));
    i.src = src;
  });
}

// set up drag/drop and input listeners
dropArea.addEventListener('click', () => fileInput.click());
dropArea.addEventListener('dragover', (e) => { e.preventDefault(); dropArea.style.background = '#222'; });
dropArea.addEventListener('dragleave', () => { dropArea.style.background = '#1A1A1A'; });
dropArea.addEventListener('drop', (e) => { e.preventDefault(); dropArea.style.background = '#1A1A1A'; const f = e.dataTransfer.files && e.dataTransfer.files[0]; if(f) handleFile(f); });
fileInput.addEventListener('change', (e) => { const f = e.target.files && e.target.files[0]; if(f) handleFile(f); });

// robust handleFile waits for image to fully load before enabling actions
async function handleFile(file) {
  if (!file) return;
  dropText.textContent = file.name;
  generateBtn.disabled = true;
  generateBtn.textContent = 'Loading image...';

  const dataUrl = await new Promise((res, rej) => { 
    const r = new FileReader();
    r.onload = () => res(r.result);
    r.onerror = () => rej(new Error('FileReader error'));
    r.readAsDataURL(file);
  });

  const img = new Image();
  img.crossOrigin = 'anonymous';
  img.src = dataUrl;
  await new Promise((res, rej) => { img.onload = res; img.onerror = rej; });

  uploadedImage = img;
  try { await updatePreviews(); } catch(e) { console.error('Preview error', e); }
  generateBtn.disabled = false;
  generateBtn.textContent = 'Generate Zip';
}

// update previews only when uploadedImage exists
async function updatePreviews() {
  if (!uploadedImage) return;
  const [sqMask, circMask] = await Promise.all([loadImage(squareMaskSrc), loadImage(circularMaskSrc)]);

  // square preview
  const sctx = squarePreview.getContext('2d');
  sctx.clearRect(0,0,120,120);
  sctx.globalCompositeOperation = 'source-over';
  sctx.drawImage(uploadedImage, 0,0,120,120);
  sctx.globalCompositeOperation = 'destination-in';
  sctx.drawImage(sqMask, 0,0,120,120);
  sctx.globalCompositeOperation = 'source-over';

  // circular preview
  const cctx = circlePreview.getContext('2d');
  cctx.clearRect(0,0,120,120);
  cctx.globalCompositeOperation = 'source-over';
  cctx.drawImage(uploadedImage, 0,0,120,120);
  cctx.globalCompositeOperation = 'destination-in';
  cctx.drawImage(circMask, 0,0,120,120);
  cctx.globalCompositeOperation = 'source-over';
}

// add custom size
addSizeBtn.addEventListener('click', () => {
  const v = parseInt(newSizeInput.value);
  if(!isNaN(v) && v>0 && !sizes.includes(v)) { sizes.push(v); alert('Added size '+v); }
  newSizeInput.value = '';
});

// helper to draw masked image and return base64 data (without data: prefix)
function drawMaskedBase64(img, maskImg, w, h) {
  const c = document.createElement('canvas');
  c.width = w; c.height = h;
  const ctx = c.getContext('2d');
  ctx.clearRect(0,0,w,h);
  ctx.drawImage(img, 0, 0, w, h);
  ctx.globalCompositeOperation = 'destination-in';
  ctx.drawImage(maskImg, 0, 0, w, h);
  return c.toDataURL('image/png').split(',')[1];
}

// generate zip on click
generateBtn.addEventListener('click', async () => {
  if(!uploadedImage) return alert('Please upload an image first');
  const platform = document.querySelector('input[name="platform"]:checked').value;

  // load masks (including ios squircle)
  const [sqMask, circMask, iosMask] = await Promise.all([loadImage(squareMaskSrc), loadImage(circularMaskSrc), loadImage(iosSquircleSrc)]);

  const zip = new JSZip();

  // always include square & circular in full sizes
  const squareFolder = zip.folder('square');
  const circularFolder = zip.folder('circular');
  for(const s of sizes) {
    const b1 = drawMaskedBase64(uploadedImage, sqMask, s, s);
    squareFolder.file(`Icon-square-${s}x${s}.png`, b1, {base64:true});

    const b2 = drawMaskedBase64(uploadedImage, circMask, s, s);
    circularFolder.file(`Icon-circular-${s}x${s}.png`, b2, {base64:true});
  }

  // platform-specific outputs
  if(platform === 'ios' || platform === 'all') {
    const f = zip.folder('iOS');
    for(const s of iosExact) {
      const b = drawMaskedBase64(uploadedImage, iosMask, s, s);
      f.file(`Icon-iOS-${s}x${s}.png`, b, {base64:true});
    }
  }

  if(platform === 'android' || platform === 'all') {
    const f2 = zip.folder('Android');
    for(const s of androidExact) {
      const full = drawMaskedBase64(uploadedImage, sqMask, s, s);
      f2.file(`Icon-Android-square-${s}x${s}.png`, full, {base64:true});

      const circ = drawMaskedBase64(uploadedImage, circMask, s, s);
      f2.file(`Icon-Android-circular-${s}x${s}.png`, circ, {base64:true});
    }
  }

  const blob = await zip.generateAsync({type:'blob'});
  const a = document.createElement('a');
  a.href = URL.createObjectURL(blob);
  a.download = 'icons-platforms.zip';
  a.click();
});

// Theme toggle + logo switching + default dark mode
const themeSwitch = document.getElementById('theme-switch');
const logoImg = document.getElementById('main-logo');

let savedTheme = localStorage.getItem('theme');
if (!savedTheme) {
  savedTheme = 'dark';
  localStorage.setItem('theme', 'dark');
}

if (savedTheme === 'light') {
  document.body.classList.add('light');
  themeSwitch.checked = true;
  logoImg.src = 'resizer-logo-black-v3.png';
} else {
  document.body.classList.remove('light');
  themeSwitch.checked = false;
  logoImg.src = 'app-icon-resizer-logo-v3.png';
}

themeSwitch.addEventListener('change', () => {
  const isLight = themeSwitch.checked;
  document.body.classList.toggle('light', isLight);
  localStorage.setItem('theme', isLight ? 'light' : 'dark');
  logoImg.src = isLight ? 'resizer-logo-black-v3.png' : 'app-icon-resizer-logo-v3.png';
});

// Smart ZIP naming logic
let uploadedFileName = '';
const originalHandleFile = handleFile;
handleFile = async function(file) {
  uploadedFileName = file.name.split('.').slice(0, -1).join('.') || 'icons';
  await originalHandleFile(file);
};
